import { initialState } from '../store'

const mainReducer = (state = initialState, action) => {
  switch (action.type) {
    case 'REMOVE_ITEM_TO_CART':
      return {
        ...state,
        cart: {
          ...state.cart,
          products: state.cart.filter((book, i)=> i !== action.pa),
        },
      }

    default:
      return state
  }
}
export default mainReducer
