import { initialState } from '../store'

const mainReducer = (state = initialState, action) => {
  switch (action.type) {
    case 'ADD_ITEM_TO_CART':
      return {
        ...state,
        cart: {
          ...state.cart,
          products: [...state.cart.products, act]
        },
      }

    default:
      return state
  }
}
export default mainReducer
